INSERT INTO `artisans` VALUES (70,'MATRCES', '', 'Matrices', '', 'Hi! I\'m Matrices, I primarily specialize in helping others learn how to build their own fursuits, with my tutorial website featuring numerous guides and patterns on DIY fursuit creation. To create the wonderful fursuit-related tutorial content, I also design and craft my own costume projects that are later sold. Refurbishing, tailoring, and illustration services are also available on occasion.', '', 'US', 'Washington', 'Whidbey Island', '', 'Toony', '', '', 'Patterns\nRefurbishing\nTailoring\nTutorials', 'Washable heads', '', 'Accepts credit cards in lieu of payment plan', '', '', '', 'http://matrices.net/', '', '', 'http://www.furaffinity.net/user/matrices', '', 'https://twitter.com/RaisedbyDogs', '', 'https://pupdates.matrices.net/', '', 'https://www.youtube.com/user/MatricesOddity', 'https://trello.com/b/Rmlp7JTU/matrices-net-queue', 'https://www.patreon.com/Matrices', 'English', 'Under \"What do you do\" may I please be listed as \"Artist-designed projects\" to describe my work as designed by the artist, instead of \"premade.\" I do not wish to market myself under the \"premade\" term, here\'s some extra information on that reasoning https://pupdates.matrices.net/post/163603236598/article-marketing-yourself-in-the-furry-fandom -- if this is not an option to change that marketing, I would prefer this to remain blank.', 'https://trello.com/b/Rmlp7JTU/', NULL, NULL);
